import React, { useState } from "react";

export default function TodoInput(props) {
    const [inputState, setInputState] = useState({text: props.text})

    return(
        <input className="my-input" type="text" onChange={handler} value={inputState}/>
    )
    function handler(e) {
        setInputState({ 
            text: e.target.value,
        })
    }
}
