import React, { useEffect, useRef, useState } from "react";
import MyBtn from "./button";
import TodoInput from "./todoInput";
import './main.css'

export default function TodoForm(props) {
    const [formState, setFormState] = useState([])
    const [inputState, setInputState] = useState('')
    let refInput = useRef(inputState)
    // useEffect(()=>props.itemsSet(formState), [formState])
    return(
        <form ref={refInput} className="my-form" onSubmit={submitter}>
            <TodoInput changer={changer}></TodoInput>
            <MyBtn></MyBtn>
        </form>
    )

    function changer(value) {
        setInputState(value)
    }
    function submitter(e) {
        e.preventDefault();
        if (inputState.length === 0) {
            return;
        }
        setFormState([...formState, {text: inputState}])
        props.itemsSet(refInput.current)
    }
}
