import React, { useState } from "react";
import MyBtn from "./button";
import TodoInput from "./todoInput";
import './main.css'

export default function TodoForm(props) {
    const [formState, setFormState] = useState({items: [], text: ''})
    return(
        <form className="my-form" onSubmit={submitter}>
            <TodoInput text={formState.text}></TodoInput>
            <MyBtn></MyBtn>
        </form>
    )

    function submitter(e) {
        e.preventDefault();
        if (formState.text.length === 0) {
            return;
        }
        setFormState({items: [...formState, {text: formState.text}]})
    }
}
