import React, { useState } from "react";
import './main.css'
import './innerlist.css'
import MyBtn from "./button";
import MyList from "./list";

export default function MyItem(props) {
    const [textState, setTextState] = useState('')
    const [doneState, setDoneState] = useState(false)
    const [openState, setOpenState] = useState(false)
    const [subtaskState, setSubtaskState] = useState([])
    const [checkedState, setCheckedState] = useState(false)

    function doneMaker() {
        // this.setState({done: !this.state.done})
        setOpenState(!openState)
    }
    function changeInput(e) {
        setTextState(e.target.value)
    }
    function innerMaker(e) {
        e.preventDefault()
        setSubtaskState([...subtaskState, {text: textState}])
    }
    function markAsDone() {
        setCheckedState(!checkedState)
        console.log(checkedState)
    }

        return ( 
            <li className="item-wrap" >
                <div className="wrap-todo">
                    <label className="item-label">

                        <input onChange={markAsDone} checked={checkedState} className="default-radio" type="checkbox"/>
                        <span className="custom-radio"></span>
                        <div className="todo-item" onClick={doneMaker} key={new Date}>{props.item.text}</div>
                    </label>
                </div>
                <div className={openState ? 'open' : 'closed'}>
                    <form className='inner-form' action="">
                        <input onChange={changeInput}  value={textState} className="inner-input" type="text"/>
                        <MyBtn onClick={innerMaker}></MyBtn>
                    </form>
                    <MyList items={subtaskState}></MyList>
                </div>
            </li>
        )

}
